quiz_recursive_sum.FunctionDef:
function sum(n) {
  return function() {
    if (n <= 0) {
      return 0; 
    } else {
      return n + sum(n - 1); 
    }
  }();
}

quiz_recursive_factorial.FunctionDef:
function factorial(n) {
  return function(result) {
    return function() {
    if (n == 0) {
    result = 1;
  } else {
    result = n * factorial(n - 1);
  }
  return result;
  }();
  }(0);
}

