quiz_sqrt_of_average.FunctionDef:
function sqrtOfAverage(x, y) {
  return Math.sqrt((x + y) / 2);
}

quiz_is_divisible_by.FunctionDef:
function isDivisibleBy(x, y) {
  return x % y == 0;
}

quiz_height_in_inches.FunctionDef:
function heightInInches(ft, inch) {
  return ft * 12 + inch;
}

quiz_hypotenuse.FunctionDef:
function hypotenuse(a, b) {
  return Math.sqrt(a * a + b * b);
}
